import React from 'react';

const Hero = () => {
  return (
    <section id="home" className="pt-24 pb-12 gradient-bg">
      <div className="container mx-auto px-4 py-16 text-center">
        <h1 className="text-4xl md:text-5xl font-bold text-white mb-6">Welcome to Sameer Distributor</h1>
        <p className="text-xl md:text-2xl text-white mb-8">Your Premier Wholesale Confectionery Partner</p>
        <div className="flex flex-col md:flex-row justify-center items-center space-y-4 md:space-y-0 md:space-x-4">
          <a href="#brands" className="bg-white text-indigo-600 px-6 py-3 rounded-lg font-semibold shadow-lg hover:bg-gray-100 transition">Our Brands</a>
          <a href="#contact" className="bg-transparent border-2 border-white text-white px-6 py-3 rounded-lg font-semibold hover:bg-white hover:text-indigo-600 transition">Contact Us</a>
        </div>
      </div>
    </section>
  );
};

export default Hero;
