import React from 'react';

const Brands = () => {
  const brands = [
    {
      name: 'Ravi Magic',
      description: 'Famous for delicious confectioneries and snacks that bring joy to consumers of all ages.',
      tag: 'Popular Choice'
    },
    {
      name: 'Britannia',
      description: 'A household name delivering trusted baked goods and confectionery products nationwide.',
      tag: 'Premium Brand'
    },
    {
      name: 'Manbhari',
      description: 'Known for traditional sweets and snacks made with authentic recipes and quality ingredients.',
      tag: 'Traditional Favorites'
    },
    {
      name: 'Balgopal',
      description: 'Specializing in sweet treats and confectionery delights that appeal to consumers of all age groups.',
      tag: 'Sweet Specialties'
    },
    {
      name: 'Vittal Mineral Water',
      description: 'Pure, refreshing mineral water sourced from natural springs and processed with advanced technology.',
      tag: 'Pure Hydration'
    },
    {
      name: 'Partner With Us',
      description: 'Interested in distributing your products through our network? We\'re always open to new partnerships.',
      tag: 'Opportunities'
    }
  ];

  return (
    <section id="brands" className="py-16 bg-white">
      <div className="container mx-auto px-4">
        <div className="text-center mb-12">
          <h2 className="text-3xl font-bold text-gray-800">Our Official Brands</h2>
          <div className="w-24 h-1 bg-indigo-600 mx-auto mt-4 mb-6"></div>
          <p className="text-gray-600 max-w-3xl mx-auto">We are proud to be the official distributor of these premium brands, known for their quality and customer satisfaction.</p>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 mt-8">
          {brands.map((brand, index) => (
            <div key={index} className="brand-card bg-white p-6 rounded-lg shadow-md border border-gray-200 transition duration-300">
              <h3 className="text-xl font-semibold mb-3 text-indigo-600">{brand.name}</h3>
              <p className="text-gray-600 mb-4">{brand.description}</p>
              <div className="flex justify-between items-center">
                <span className="text-sm text-gray-500">{brand.tag}</span>
                <a href={brand.name === 'Partner With Us' ? '#contact' : '#products'} className="text-indigo-600 hover:text-indigo-800">
                  {brand.name === 'Partner With Us' ? 'Contact Us →' : 'View Products →'}
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Brands;
